function toggleAboutMe() {
    const aboutme = document.getElementById('aboutme');
    const aboutprojects = document.getElementById('aboutprojects');

    const aboutmebtn = document.getElementById('aboutmebtn');
    const aboutprojectsbtn = document.getElementById('aboutprojectsbtn');

    if (aboutme.classList.contains('hidden')) {
        aboutme.classList.remove('hidden');
        aboutprojects.classList.add('hidden');

        aboutmebtn.classList.add('active');
        aboutprojectsbtn.classList.remove('active');
    } else {
        aboutme.classList.add('hidden');
        aboutprojects.classList.remove('hidden');

        aboutmebtn.classList.remove('active');
        aboutprojectsbtn.classList.add('active');
    }
}

function toggleProjects() {
    const projects = document.getElementById('projectsgrid');
    const certificates = document.getElementById('certificates');

    const projectsbtn = document.getElementById('projectsbtn');
    const certificatesbtn = document.getElementById('certificatesbtn');

    if (projects.classList.contains('hidden')) {
        projects.classList.remove('hidden');
        certificates.classList.add('hidden');

        projectsbtn.classList.add('active');
        certificatesbtn.classList.remove('active');
    } else {
        projects.classList.add('hidden');
        certificates.classList.remove('hidden');

        projectsbtn.classList.remove('active');
        certificatesbtn.classList.add('active');
    }
}

document.addEventListener('DOMContentLoaded', function () {
    const translatebtn = document.getElementById('translatebtn');
    translatebtn.addEventListener('click', translateText);
});

function translateText() {
    const czech = document.getElementsByClassName('czech');
    const english = document.getElementsByClassName('english');

    console.log(czech + '[A]' + english);

    if (english[0].classList.contains('activelang')) {
        for (const englishElement of english) {
            englishElement.classList.remove('activelang');
        }
        for (const czechElement of czech) {
            czechElement.classList.add('activelang');
            if (czechElement.classList.contains('double')) {
                czech.classList.add('double');
            }
        }
    } else {
        for (const englishElement of english) {
            englishElement.classList.add('activelang');
            if (englishElement.classList.contains('double')) {
                englishElement.classList.add('double');
            }
        }
        for (const czechElement of czech) {
            czechElement.classList.remove('activelang');
        }
    }
}
